import matplotlib.pyplot as plt
import numpy as np

from scipy import stats


def do_picture(x, y):
    try:
        res = stats.linregress(x, y)
        R_squared = res.rvalue ** 2

        plt.plot(0, 0, 'w', label='R\u00b2 = ' + str(R_squared))
        plt.plot(x, y, 'b', label='Наши данные')
        plt.plot(x, res.intercept + res.slope * x, 'r', label='Линия тренда')
        plt.legend()

        plt.savefig('F:\\hackaton\\pythonBot_2.0\\pictures\\picture.png')
        plt.close()
    except:
        return False
    
    return True
